mapper.select:
SELECT system_id, system_name, type, operate_system, version, system_bit, system_disk, user_id, user_name, system_format, system_slim,system_size,
       to_char(upload_time, 'YYYY-MM-DD HH24:MI:SS') as upload_time, status, to_char(create_time, 'YYYY-MM-DD HH24:MI:SS') as create_time,
       error_type
FROM tbl_vm_system


        and (user_id = #{userId} or type = 'public')


        and operate_system in

            #{item}



        and lower(system_name) like lower(CONCAT('%', #{systemName}, '%'))


        and status in

            #{item}



        and type in

            #{item}



         and upload_time >= to_timestamp(#{uploadTimeBegin}, 'YYYY-MM-DD HH24:MI:SS') 


         and upload_time <= to_timestamp(#{uploadTimeEnd}, 'YYYY-MM-DD HH24:MI:SS') 




        order by ${queryCtrl.sortBy} ${queryCtrl.sortOrder}


        offset #{queryCtrl.offset} limit #{queryCtrl.limit}

SELECT COUNT(1) FROM tbl_vm_system WHERE system_name = #{systemName}

    and user_id = #{userId}


    and system_id != #{systemId}

mapper.update:
UPDATE tbl_vm_system
SET system_name=#{systemName}, type=#{type},
    operate_system=#{operateSystem}, version=#{version},
    system_bit=#{systemBit}, system_disk=#{systemDisk}, modify_time=now()
WHERE system_id = #{systemId}

    and user_id = #{userId}

UPDATE tbl_vm_system
SET status = #{status}, system_path = #{systemPath}, file_name=#{fileName},
    file_md5=#{fileMd5}, system_format=#{fileFormat}, upload_time=now(),
    system_size=#{fileSize}
WHERE system_id = #{systemId}

UPDATE tbl_vm_system
SET file_md5=#{fileMd5}, system_size=#{fileSize}
WHERE system_id = #{systemId}
